import React, { Component } from 'react'
import Moment from 'react-moment';
import 'moment-timezone';

class ShowDateTime extends Component {

    state = {
        date: new Date(),
    }
    callMe() {
        setInterval(() => {
            this.setState({ date: new Date() });
        }, 1000);
    }

    render() {
        return (
            <div className="flex justify-center ">
                <div className="tabular-nums rounded-full px-4 text-xs mr-2 py-1 mt-5 text-lg ">
                    <div className="bg-yellow-300 text-gray-700 font-semibold py-2 px-4 rounded inline-flex items-center">
                        {this.state.date.toLocaleTimeString()} | {this.state.date.toLocaleDateString()}
                    </div>
                </div>
                {this.callMe()}
            </div>

        );
    }
}

export default ShowDateTime;
