import React,{ChangeEvent, useState}  from 'react';
import { useSelector } from "react-redux"
import { createStyles, withStyles, WithStyles, makeStyles, Theme } from '@material-ui/core/styles';
import Dialog from '@material-ui/core/Dialog';
import MuiDialogTitle from '@material-ui/core/DialogTitle';
import MuiDialogContent from '@material-ui/core/DialogContent';
import IconButton from '@material-ui/core/IconButton';
import CloseIcon from '@material-ui/icons/Close';
import Typography from '@material-ui/core/Typography';
import TextField from '@material-ui/core/TextField';
import useCheckInAction from '../../hooks/useCheckInAction'
import { RootState } from '../../store/reducers';



const styles = (theme: Theme) =>
  createStyles({
    root: {
      margin: 0,
      padding: theme.spacing(2),
    },
    closeButton: {
      position: 'absolute',
      right: theme.spacing(1),
      top: theme.spacing(1),
      color: theme.palette.grey[500],
    },
  });
  const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    container: {
      display: 'flex',
      flexWrap: 'wrap',
    },
    textField: {
      marginLeft: theme.spacing(1),
      marginRight: theme.spacing(1),
      width: 200,
    },
  }),
);
  export interface DialogTitleProps extends WithStyles<typeof styles> {
    id: string;
    children: React.ReactNode;
    onClose: () => void;
  }
  const DialogTitle = withStyles(styles)((props: DialogTitleProps) => {
    const { children, classes, onClose, ...other } = props;
      return (
      <MuiDialogTitle disableTypography className={classes.root} {...other}>
        <Typography variant="h6">{children}</Typography>
        {onClose ? (
          <IconButton aria-label="close" className={classes.closeButton} onClick={onClose}>
            <CloseIcon />
          </IconButton>
        ) : null}
      </MuiDialogTitle>
    );
  });
  const DialogContent = withStyles((theme: Theme) => ({
    root: {
      padding: theme.spacing(2),
    },
  }))(MuiDialogContent);


  export default function CustomizedDialogs() {

    //////useCheckInAction////////
    const {postTimeToday} = useCheckInAction()
    const { clearNoteAndExtratime ,clearPassword } = useCheckInAction()

    ///////Dialog////////////////
    const [open, setOpen] = React.useState(false);
    const handleClickOpen = () => {
      setOpen(true);
    };
    const handleClose = () => {
      setOpen(false);
    };
    const classes = useStyles();

    //////Function/////////////////
    const numPassword = useSelector((state: RootState) => state.checkIn.password)
    const  [note ,setNote]= useState("")

    const [timeStart, setTimeStart] = useState("");
    const [timeEnd , setTimeEnd] = useState("");

    const onChangeNote = (event: ChangeEvent<HTMLTextAreaElement>) => {
      console.log("event.target.value", event.target.value)
      setNote(event.target.value)
    }
    function postOtherAttendace() {
      postTimeToday(note)
      clearNoteAndExtratime()
      handleClose()
    }    
    function postAttendaceToday() {
      if(numPassword.length == 4 ){
        postOtherAttendace()
        postTimeToday(numPassword, {
          note,
          // extratimeStart:timeStart,
          // extratimeFinish:timeEnd,
        })
        clearPassword()
      }
    }
  return (
    <div>
      <button  
        className="bg-yellow-300 text-gray-700 font-semibold py-3.5 px-16 rounded inline-flex items-center text-xl"
        onClick={handleClickOpen}
        >  
         Attendance
        </button>
      <Dialog onClose={handleClickOpen} aria-labelledby="customized-dialog-title" open={open}>
        <DialogTitle id="customized-dialog-title" onClose={handleClose}>
        <div className="flex items-center space-x-5">
          <div className="h-14 w-14 bg-yellow-200 rounded-full flex flex-shrink-0 justify-center items-center text-yellow-500 text-2xl font-mono">M</div>
            <div className="block pl-2 font-semibold text-xl self-start text-gray-700">
              <h2 className="leading-relaxed">Other Time Attendance</h2>
              <p className="text-sm text-gray-500 font-normal leading-relaxed">Lorem ipsum, dolor sit amet consectetur adipisicing elit.</p>
            </div> 
        </div>
        </DialogTitle>
        <DialogContent dividers>
          <form >
                <div className=" ">
                    <label className="leading-loose mt-5 pl-2">#Note</label>   
                          <TextField 
                              name="Note"
                              id="outlined-multiline-static"
                              multiline
                              rows={4}
                              variant="outlined"
                              type="text" 
                              className="px-4 py-2 border 
                              focus:ring-gray-500 focus:border-gray-900 w-full sm:text-sm 
                              border-gray-300 rounded-md focus:outline-none text-gray-600" 
                              placeholder="Note"
                              onChange={(event) => {
                                setTimeStart(event.target.value)
                              }}
                           />
                </div>
                <div className="relative mt-10 h-px bg-gray-300">
                      <div className="absolute left-0 top-0 flex justify-center w-full -mt-2">
                        <span className="bg-white px-4 text-xs text-gray-500 uppercase">Extratime</span>
                      </div>
                </div>
                <br/>
                <div className="flex items-center space-x-4">
                    <div className="flex flex-col">
                      <label className="leading-loose pl-3 ">Time-Start</label>
                        <div className="relative focus-within:text-gray-600 text-gray-400">
                          <form className={classes.container} noValidate>
                            <TextField   
                                id="time"
                                type="time"
                                variant="outlined"
                                defaultValue="00:00"
                                className={classes.textField}
                                InputLabelProps={{
                                shrink: true,
                                }}
                                inputProps={{
                                step: 300, // 5 min
                                }}
                                onChange={(event) => {
                                  setTimeStart(event.target.value)
                                }}
                              />
                          </form>
                      </div>
                    </div>
                    <div className="flex flex-col">
                      <label className="leading-loose pl-3">Time-End</label>
                      <div className="relative focus-within:text-gray-600 text-gray-400">
                        <form className={classes.container} noValidate>
                            <TextField
                                id="time"
                                type="time"
                                variant="outlined"
                                defaultValue="00:00"
                                className={classes.textField}
                                InputLabelProps={{
                                shrink: true,
                                }}
                                inputProps={{
                                step: 300, // 5 min
                                }}
                                onChange={(event) => {
                                  setTimeEnd(event.target.value)
                                }}
                              />
                        </form>
                      </div>
                    </div>
                  </div>
                  <div className="relative mt-10 h-px bg-gray-300">
                      <div className="absolute left-0 top-0 flex justify-center w-full -mt-2">
                        <span className="bg-white px-4 text-xs text-gray-500 uppercase"></span>
                      </div>
                </div>
                </form>
                  <br/>
                  <div className="pt-4 flex items-center space-x-4">
                        <button 
                        onClick ={clearNoteAndExtratime}
                        className="flex justify-center items-center w-full 
                        text-gray-900 px-4 py-3 rounded-md 
                        focus:outline-none"
                        >
                          <svg className="w-6 h-6 mr-3" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M6 18L18 6M6 6l12 12"></path></svg> 
                          Clear
                        </button>
                        <button 
                        onClick={postAttendaceToday}
                        className="bg-yellow-400 flex justify-center items-center w-full 
                        text-white px-4 py-3 rounded-md focus:outline-none">
                          Save
                        </button>
                </div>
        </DialogContent>
      </Dialog>
    </div>
  );
}
