import { useCallback } from 'react'
import { useDispatch } from 'react-redux'
import {addPasswordDispatch, resetPasswordDispatch, getEmployeeTimesDispatch,postEmployeeTimesDispatch} from '../store/actions/checkIn'

function useCheckInAction() {
    const dispatch = useDispatch()

    const addNumpadPassword =  useCallback((pass: string) => {
        dispatch(addPasswordDispatch(pass))
    }, [dispatch])

    const clearPassword = useCallback(
        () => {
            dispatch(resetPasswordDispatch())
        },
        [dispatch],
    )

    const getTimeOfEmpsToday = useCallback(
        () => {
            dispatch(getEmployeeTimesDispatch())
        },
        [dispatch],
    )
    const postEmployeeTimesToday = useCallback(
        () =>{
            dispatch(postEmployeeTimesDispatch())
        },

    )
    return {
        addNumpadPassword,
        clearPassword,
        getTimeOfEmpsToday
    }
}

export default useCheckInAction
