import React, { useEffect, useState } from "react"
import { useSelector } from "react-redux"
import styled from "styled-components"
import TableTime from '../../components/Dashboard/TableTime'
import Numpad from "../../components/Numpad/NumPad"
import ShowDateTime from "../../components/ShowDateTime"
import ShowPassword from '../../components/ShowPassword/ShowPassword'
import useCheckInAction from "../../hooks/useCheckInAction"
import { RootState } from "../../store/reducers"
import Snackbar from '@material-ui/core/Snackbar';



const Container = styled.div`
  display: flex;
  flex-direction: row;
  background-color: rgba(255,255,255,0.5);
  height: 100%px;
  width: 100%px;
`;

const LayoutNumpad = styled.div`
  flex: 3 1 0%;
  background-color: rgba(240, 240, 240,1);
  display: flex;
  flex-direction: column;
`;

const CheckIn = () => {

    const numPassword = useSelector((state: RootState) => state.checkIn.password)
    const [open, setOpen] = useState<boolean>(false)
    const { clearPassword, postTimeToday } = useCheckInAction()

    useEffect(() => {
        if (numPassword.length >= 4) {

            postTimeToday(numPassword)
            setOpen(true)
            clearPassword()
        }
    }, [numPassword, clearPassword, setOpen, postTimeToday])

    return (
        <Container>

            <TableTime />
            <LayoutNumpad>
                <ShowDateTime />
                <div className="px-8">
                    <ShowPassword password={numPassword} />
                </div>
                <Numpad />
            </LayoutNumpad>
        </Container>
    )
}

export default CheckIn
