import React, { useEffect, useState } from "react"
import { useSelector } from "react-redux"
import styled from "styled-components"
import TableTime from '../../components/Dashboard/TableTime'
import Numpad from "../../components/Numpad/NumPad"
import ShowDateTime from "../../components/ShowDateTime"
import ShowPassword from '../../components/ShowPassword/ShowPassword'
import useCheckInAction from "../../hooks/useCheckInAction"
import { RootState } from "../../store/reducers"
import Snackbar from '@material-ui/core/Snackbar';
import Swal from 'sweetalert2'
import withReactContent from 'sweetalert2-react-content'


const Container = styled.div`
  display: flex;
  flex-direction: row;
  background-color: rgba(255,255,255,0.5);
  height: 100%px;
  width: 100%px;
`;

const LayoutNumpad = styled.div`
  flex: 3 1 0%;
  background-color: rgba(240, 240, 240,1);
  display: flex;
  flex-direction: column;
`;

const CheckIn = () => {

    const numPassword = useSelector((state: RootState) => state.checkIn.password)
    const [open, setOpen] = useState<boolean>(false)
    const { clearPassword } = useCheckInAction()
    const MySwal = withReactContent(Swal)


    useEffect(() => {
        if (numPassword.length >= 4) {
            MySwal.fire({
                title: <p>Hello World</p>,
                footer: 'Copyright 2018',
                didOpen: () => {
                    // `MySwal` is a subclass of `Swal`
                    //   with all the same instance & static methods
                    MySwal.clickConfirm()
                  }
            })
            setOpen(true)
            clearPassword()
        }
    }, [numPassword, clearPassword, setOpen])

    return (
        <Container>
            <Snackbar
                anchorOrigin={{ vertical: 'top', horizontal: 'center' }}
                open={open}
                onClose={() => setOpen(false)}
                autoHideDuration={3000}
            />

            <TableTime />
            <LayoutNumpad>
                <ShowDateTime />
                <div className="px-8">
                    <ShowPassword password={numPassword} />
                </div>
                <Numpad />
            </LayoutNumpad>
        </Container>
    )
}

export default CheckIn
