import styled from "styled-components";
import React,{useEffect} from 'react'
import { useSelector} from 'react-redux'
import useCheckInAction from '../../hooks/useCheckInAction'
import {RootState} from '../../store/reducers'
import Table from '@material-ui/core/Table';
import TableCell from '@material-ui/core/TableCell';
import ITimeToday from "../../types/ITimeToday";
import classNames from 'classnames'
import ShowMomentTime from "./ShowMomentTime";
import TableScrollbar from 'react-table-scrollbar';







const EMP_TIME_TODAY_ID = 'emp-time-today-'
function empsTodayByCheckInSelector(state: RootState) {

  const todayEmps = state.checkIn.todayEmps || []
  return todayEmps.sort((a: ITimeToday, b: ITimeToday) => {
    if (a.checkIn && b.checkIn) {
      if (a.checkIn < b.checkIn)
        return -1
    }
    return 0
  })
}

const LayoutDashboard = styled.div`
  flex: 7 1 0%;
  background-color: rgba(255,214,107,1);
  display: flex;

  flex-direction: column;
  .selected-row {
    background-color: #F0F0F0;
  }

`;
function TableTime() {

    const {getTimeOfEmpsToday} = useCheckInAction()
    const emps = useSelector(empsTodayByCheckInSelector)
    const latestEmpCheckIn = useSelector((state: RootState) => state.checkIn.latest)

    /////Page row///////
    const [page, setPage] = React.useState(0);
    const [rowsPerPage, setRowsPerPage] = React.useState(11);

    /////Change Page///////
    const handleChangePage = (event: unknown, newPage: number) => {
      setPage(newPage);
    };
    const handleChangeRowsPerPage = (event: React.ChangeEvent<HTMLInputElement>) => {
      setRowsPerPage(+event.target.value);
      setPage(0);
    };
    ///// Hook ///////
    useEffect(() => {
        getTimeOfEmpsToday();
    }, [getTimeOfEmpsToday])
    console.log("emps emps:", emps)

    useEffect(() => {
      if (latestEmpCheckIn) {
        const curEle = document.getElementById(`${EMP_TIME_TODAY_ID}${latestEmpCheckIn.employeeId}`)
        if (curEle){
          curEle.scrollIntoView()
        }
      }
    }, [latestEmpCheckIn])


      return (
        <LayoutDashboard className="h-screen">
          <div className="md:px-10 mt-0.25 py-8 w-full ">
                    <div className="shadow overflow-hidden rounded border-b border-gray-200">
                        <Table className="min-w-full bg-white" stickyHeader aria-label="sticky table">
                                <thead className="bg-gray-800 text-white ">
                                <tr>
                                    <th className="text-left py-3 px-4 uppercase font-semibold text-sm">No</th>
                                    <th className="text-left py-3 px-4 uppercase font-semibold text-sm">Name</th>
                                    <th className="text-left py-3 px-4 uppercase font-semibold text-sm"> Check-In</th>
                                    <th className="text-left py-3 px-4 uppercase font-semibold text-sm">Check-Out</th>
                                    <th className="text-left py-3 px-4 uppercase font-semibold text-sm">#Note</th>
                                </tr>
                                </thead>
                                    <tbody className="text-gray-700  ">
                                        {emps.length > 0 ? emps.slice(page* rowsPerPage,page * rowsPerPage + rowsPerPage).map((emp: ITimeToday, index: number) => {
                                          return (
                                            <tr
                                              tabIndex={-1} 
                                              key={emp.employeeId}
                                              id={`${EMP_TIME_TODAY_ID}${emp.employeeId?.toString()}`}
                                              className={classNames({"selected-row": emp.employeeId === latestEmpCheckIn?.employeeId})}
                                            >
                                              <TableCell className="text-left py-3 px-4">{index + 1}</TableCell>
                                              <TableCell className="text-left py-3 px-4">{emp.nickName}</TableCell>
                                              <TableCell className="text-left py-3 px-4"><ShowMomentTime time={emp.checkIn} /></TableCell>
                                              <TableCell className="text-left py-3 px-4"><ShowMomentTime time={emp.checkOut} /></TableCell>
                                              <TableCell className="text-left py-3 px-4">{emp.note}</TableCell>
                                            </tr>
                                          );
                                        }) : <tr>
                                              <TableCell colSpan={5} className="justify-center">{'no employee today'}</TableCell>
                                          </tr>}
                                    </tbody>                       
                        </Table>
                    </div>
                </div>
        </LayoutDashboard>
        )
    }
export default TableTime
