import React, { useEffect, useState } from "react"
import Button from '@material-ui/core/Button';
import useCheckInAction from "../../hooks/useCheckInAction"
import { RootState } from "../../store/reducers"
import { useSelector } from "react-redux"


const  BtnAttendances = () =>{
    const numPassword = useSelector((state: RootState) => state.checkIn.password)
    const postTimeToday : any = useCheckInAction
    useEffect(() => {
        if (numPassword.length >= 4) {
            postTimeToday(numPassword)
        }
    }, [numPassword, postTimeToday])

    return(
        <button  onClick={ postTimeToday(numPassword) } className="bg-yellow-300 text-gray-700 font-semibold py-2 px-16 rounded inline-flex items-center text-xl">  
         Attendance
        </button>
    )

}

export default TableTime
