import { useCallback } from 'react'
import { useDispatch } from 'react-redux'
import {addPasswordDispatch, 
        resetPasswordDispatch, 
        getEmployeeTimesDispatch, 
        postTimeDispatch, 
        deletePasswordDispatch,
        resetNoteandExtratimeDispatch
        ,addNoteDispatch,} 
        from '../store/actions/checkIn'

function useCheckInAction() {
    const dispatch = useDispatch()

    const addNumpadPassword =  useCallback((pass: string) => {
        dispatch(addPasswordDispatch(pass))
    }, [dispatch])

    const addNote =  useCallback(
        (note: string) => {
        dispatch(addNoteDispatch(note))
    }, [dispatch])

    const clearPassword = useCallback(
        () => {
            dispatch(resetPasswordDispatch())
        },
        [dispatch],
    )
    const clearNoteAndExtratime = useCallback(
        () => {
            dispatch(resetNoteandExtratimeDispatch())
        },[dispatch],
    )
    const deletePassword = useCallback(
        () => {
            dispatch(deletePasswordDispatch())
        },
        [dispatch],
    )
    const getTimeOfEmpsToday = useCallback(
        () => {
            dispatch(getEmployeeTimesDispatch())
        },
        [dispatch],
    )
    const postTimeToday = useCallback(
        (password, timeEmp? ,note? ) =>{
            dispatch(postTimeDispatch(password, timeEmp , note  ))
        },
        [dispatch],
    )
    return {
        addNumpadPassword,
        addNote,
        clearPassword,
        clearNoteAndExtratime,
        getTimeOfEmpsToday,
        postTimeToday,
        deletePassword
    }
}

export default useCheckInAction
