import React, { useEffect, useState } from "react"
import useCheckInAction from "../../hooks/useCheckInAction"
import { RootState } from "../../store/reducers"
import { useSelector } from "react-redux"
import Snackbar from '@material-ui/core/Snackbar';
import { Alert } from "@material-ui/lab"

const  BtnAttendances = () =>{
    const numPassword = useSelector((state: RootState) => state.checkIn.password)
    const [open, setOpen] = useState<boolean>(false)
    const { clearPassword, postTimeToday } = useCheckInAction()

    const handleClose = (event?: React.SyntheticEvent, reason?: string) => {
        if (reason === 'clickaway') {
          return;
        }
        if (reason === 'clickaway') {
            return;
          }
        setOpen(false);
        };
    function postTimeattendance() {
        if (numPassword.length == 4) {
            postTimeToday(numPassword)
            setOpen(true)
            clearPassword()
        }         
    }

    return(
        <button  
        className="bg-yellow-300 text-gray-700 font-semibold py-3.5 px-16 rounded inline-flex items-center text-xl"
        onClick={postTimeattendance}
        >  
         Attendance
         <Snackbar
                anchorOrigin={{ vertical: 'top', horizontal: 'center'}}
                open={open}
                message="Check-In succes!"
                autoHideDuration={6000}
            >
            <Alert 
                onClose={handleClose} 
                severity="success">
                 Check-In success !
            </Alert>        
            </Snackbar>
        </button>

    )

}

export default BtnAttendances
