import React, { useEffect, useState } from "react"
import useCheckInAction from "../../hooks/useCheckInAction"
import { RootState } from "../../store/reducers"
import { useSelector } from "react-redux"


const  BtnAttendances = () =>{
    const numPassword = useSelector((state: RootState) => state.checkIn.password)
    const postTimeToday : any = useCheckInAction

    function postTimeattendance() {
        if (numPassword.length >= 4) {
            postTimeToday(numPassword)
            setOpen(true)
            clearPassword()
        }
    }

    return(
        <button  
        className="bg-yellow-300 text-gray-700 font-semibold py-3.5 px-16 rounded inline-flex items-center text-xl"
        onC
        >  
         Attendance
        </button>
    )

}

export default BtnAttendances
