import React, { useEffect, useState } from "react"
import { useSelector } from "react-redux"
import styled from "styled-components"
import TableTimetoday from '../../components/Dashboard/TableTimetoday'
import Numpad from "../../components/Numpad/NumPad"
import ShowDateTime from "../../components/ShowDateTime"
import ShowPassword from '../../components/ShowPassword/ShowPassword'
import useCheckInAction from "../../hooks/useCheckInAction"
import { RootState } from "../../store/reducers"
import Snackbar from '@material-ui/core/Snackbar';
import { Alert } from "@material-ui/lab"






const Container = styled.div`
  display: flex;
  flex-direction: row;
  background-color: rgba(255,255,255,0.5);
  height: 100%px;
  width: 100%px;
`;

const LayoutNumpad = styled.div`
  flex: 3 1 0%;
  background-color: rgba(240, 240, 240,1);
  display: flex;
  flex-direction: column;
`;


const CheckIn = () => {

    const numPassword = useSelector((state: RootState) => state.checkIn.password)
    const [open, setOpen] = useState<boolean>(false)
    const { clearPassword, postTimeToday } = useCheckInAction()

    const handleClose = (event?: React.SyntheticEvent, reason?: string) => {
        if (reason === 'clickaway') {
          return;
        }
        setOpen(false);
        };

    useEffect(() => {
        if (numPassword.length >= 4) {

            postTimeToday(numPassword)
            setOpen(true)
            clearPassword()
        }
        if (numPassword.length >= 4) {
            postTimeToday(numPassword)
            setOpen(true)
            clearPassword()
        }
    }, [numPassword, clearPassword, setOpen, postTimeToday])

    return (
        <Container>
            <Snackbar
                anchorOrigin={{ vertical: 'top', horizontal: 'center'}}
                open={open}
                message="Check-In succes!"
                autoHideDuration={6000}
            >
            <Alert 
                onClose={handleClose} 
                severity="success">
                 Check-In success !
            </Alert>
            </Snackbar>
            <TableTimetoday />
            <LayoutNumpad>
                <ShowDateTime />
                <div className="px-8">
                    <ShowPassword password={numPassword} />
                </div>
                <Numpad />
            </LayoutNumpad>
        </Container>
    )
}

export default CheckIn
